mapper.select:
select name,id,inputdate
from member
where name=#{name} and tel=#{tel} and identifier=#{identifier}

select id
from business
where name=#{name} and tel=#{tel} and identifier=#{identifier}

select password
from member
where id=#{id} and name=#{name} and identifier=#{identifier}

select password
from business
where id=#{id} and name=#{name} and identifier=#{identifier}

mapper.update:
update member
set password=#{password}
where password=#{password}

update business
set password=#{password}
where password=#{password}
